mapper.select:
SELECT a.id_mechanic as idMechanic
FROM mechanic a
WHERE a.id_seller = #{idSeller}

SELECT a.id_mechanic as idMechanic, a.id_city as idCity, a.id_seller as idSeller, a.location as location, a.shop_name as shopName, a.shop_phone_number as shopPhoneNumber, a.stars as stars, a.stars_count as starsCount, a.verification_path as verificationPath, a.status as status, a.due_date_payment as dueDatePayment FROM mechanic a INNER JOIN seller s on a.id_seller = s.id_seller WHERE a.status = 1 and s.status = 1
and a.shop_name like CONCAT('%',#{search},'%')
LIMIT #{n} OFFSET #{i}

SELECT a.id_mechanic as idMechanic, a.id_city as idCity, a.id_seller as idSeller, a.location as location, a.shop_name as shopName, a.shop_phone_number as shopPhoneNumber, a.stars as stars, a.stars_count as starsCount, a.verification_path as verificationPath, a.status as status, a.due_date_payment as dueDatePayment FROM mechanic a INNER JOIN seller s on a.id_seller = s.id_seller WHERE a.status = 2 and s.status = 1
and a.shop_name like CONCAT('%',#{search},'%')
LIMIT #{n} OFFSET #{i}
